package execise6;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Addition addObj = new Addition();
		addObj.add();
	}

	private void add() {
		// TODO Auto-generated method stub
		int no = 1;
		int total = 1;
		while(no<=5)
		{
			total = total*no;
			no=no+1;
			
		}
		System.out.println(total);
	}
	
}
	
		
		
		
			
	


